INSERT INTO `config_info` VALUES (188, 'laputa-upms-service.yml', 'DEFAULT_GROUP', '## spring security 配置\nsecurity:\n  oauth2:\n    client:\n      client-id: ENC(wftU2ukw3eX1kPyOwfWG4w==)\n      client-secret: ENC(wftU2ukw3eX1kPyOwfWG4w==)\n      scope: server\n      ignore-urls:\n        - /error\n        - /druid/**\n        - /actuator/**\n        - /v2/api-docs\n\n# 数据源\nspring:\n  autoconfigure:\n    exclude: org.springframework.cloud.gateway.config.GatewayAutoConfiguration,org.springframework.cloud.gateway.config.GatewayClassPathWarningAutoConfiguration\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      driver-class-name: com.mysql.cj.jdbc.Driver\n      username: ${MYSQL_USER:root}\n      password: ${MYSQL_PWD:Laputa@123456}\n      url: jdbc:mysql://${MYSQL_HOST:laputa-mysql}:${MYSQL_PORT:3306}/${MYSQL_DB:laputa_iot_upms}?characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=false&useJDBCCompliantTimezoneShift=true&useLegacyDatetimeCode=false&serverTimezone=GMT%2B8&allowMultiQueries=true&allowPublicKeyRetrieval=true\n      stat-view-servlet:\n        enabled: true\n        allow: \"\"\n        url-pattern: /druid/*\n        #login-username: admin\n        #login-password: admin\n      filter:\n        stat:\n          enabled: true\n          log-slow-sql: true\n          slow-sql-millis: 10000\n          merge-sql: false\n        wall:\n          config:\n            multi-statement-allow: true\n\n\n\n# Logger Config\nlogging:\n  level:\n    com.laputa.iot.admin.mapper: debug\n\n# 租户表维护\nlaputa:\n  tenant:\n    column: tenant_id\n    tables:\n      - sys_user\n      - sys_role\n      - sys_menu\n      - sys_dept\n      - sys_log\n      - sys_social_details\n      - sys_dict\n      - sys_dict_item\n      - sys_public_param\n      - sys_log\n      - sys_oauth_client_details\n', '2308ce83637cd8874a5e0d18e1f8cacf', '2021-07-13 14:28:01', '2021-10-10 12:56:27', 'nacos', '222.191.229.234', '', 'laputa-iot-dev', 'laputa iot upms的基本配置信息', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (198, 'common.yml', 'DEFAULT_GROUP', 'server:\r\n  undertow: # jetty  undertow\r\n    io-threads: 8 # 设置IO线程数, 它主要执行非阻塞的任务,它们会负责多个连接, 默认设置每个CPU核心一个线程\r\n    worker-threads: 120  # 阻塞任务线程池, 当执行类似servlet请求阻塞操作, undertow会从这个线程池中取得线程,它的值设置取决于系统的负载\r\n    buffer-size: 2048  # 以下的配置会影响buffer,这些buffer会用于服务器连接的IO操作,有点类似netty的池化内存管理 , 每块buffer的空间大小,越小的空间被利用越充分\r\n    direct-buffers: true  # 是否分配的直接内存\r\n\r\nspring:\r\n  http:\r\n    encoding:\r\n      charset: UTF-8\r\n      force: true\r\n      enabled: true\r\n  servlet:\r\n    multipart:\r\n      max-file-size: 512MB      # Max file size，默认1M\r\n      max-request-size: 512MB   # Max request size，默认10M\r\n\r\ndozer:\r\n  mappingFiles:\r\n    - classpath:dozer/global.dozer.xml\r\n    - classpath:dozer/biz.dozer.xml\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      base-path: /actuator\r\n      exposure:\r\n        include: \'*\'\r\n  endpoint:\r\n    health:\r\n      show-details: ALWAYS\r\n      enabled: true\r\n\r\nfeign:\r\n  httpclient:\r\n    enabled: false\r\n  okhttp:\r\n    enabled: true\r\n  hystrix:\r\n    enabled: true   # feign 熔断机制是否开启\r\n    #支持压缩的mime types\r\n  compression:  # 请求压缩\r\n    request:\r\n      enabled: true\r\n      mime-types: text/xml,application/xml,application/json\r\n      min-request-size: 2048\r\n    response:  # 响应压缩\r\n      enabled: true\r\n\r\nribbon:\r\n  httpclient:\r\n    enabled: false\r\n  okhttp:\r\n    enabled: true\r\n  eureka:\r\n    enabled: true\r\n  ReadTimeout: 30000     #\r\n  ConnectTimeout: 30000  # [ribbon超时时间]大于[熔断超时],那么会先走熔断，相当于你配的ribbon超时就不生效了  ribbon和hystrix是同时生效的，哪个值小哪个生效\r\n  MaxAutoRetries: 0             # 最大自动重试\r\n  MaxAutoRetriesNextServer: 1   # 最大自动像下一个服务重试\r\n  OkToRetryOnAllOperations: false  #无论是请求超时或者socket read timeout都进行重试，\r\n\r\nhystrix:\r\n  threadpool:\r\n    default:\r\n      coreSize: 1000 # #并发执行的最大线程数，默认10\r\n      maxQueueSize: 1000 # #BlockingQueue的最大队列数\r\n      queueSizeRejectionThreshold: 500 # #即使maxQueueSize没有达到，达到queueSizeRejectionThreshold该值后，请求也会被拒绝\r\n  command:\r\n    default:\r\n      execution:\r\n        isolation:\r\n          thread:\r\n            timeoutInMilliseconds: 120000  # 熔断超时 ribbon和hystrix是同时生效的，哪个值小哪个生效\r\n\r\nid-generator:\r\n  machine-code: 1  # id生成器机器掩码', 'f33766c1ef74680c95b4bf4b107d0d52', '2021-07-22 16:04:32', '2021-07-22 16:04:32', NULL, '222.191.229.234', '', 'pinda-dev', NULL, NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (199, 'mysql.yml', 'DEFAULT_GROUP', '# mysql 个性化配置， 不同的环境，需要配置不同的链接信息，只需要将这段信息复制\r\n# 到具体环境的配置文件中进行修改即可\r\n# 如：复制到pd-auth-server-dev.yml中将数据库名和ip改掉\r\npinda:\r\n  mysql:\r\n    ip: 127.0.0.1\r\n    port: 3306\r\n    driverClassName: com.mysql.cj.jdbc.Driver\r\n    database: pd_auth\r\n    username: root\r\n    password: root\r\n  database:\r\n    isBlockAttack: false  # 是否启用 攻击 SQL 阻断解析器\r\n\r\n# mysql 通用配置\r\nspring:\r\n  datasource:\r\n    druid:\r\n      username: ${pinda.mysql.username}\r\n      password: ${pinda.mysql.password}\r\n      driver-class-name: ${pinda.mysql.driverClassName}\r\n      url: jdbc:mysql://${pinda.mysql.ip}:${pinda.mysql.port}/${pinda.mysql.database}?serverTimezone=CTT&characterEncoding=utf8&useUnicode=true&useSSL=false&autoReconnect=true&zeroDateTimeBehavior=convertToNull&allowMultiQueries=true\r\n      db-type: mysql\r\n      initialSize: 10\r\n      minIdle: 10\r\n      maxActive: 500\r\n      max-wait: 60000\r\n      pool-prepared-statements: true\r\n      max-pool-prepared-statement-per-connection-size: 20\r\n      validation-query: SELECT \'x\'\r\n      test-on-borrow: false\r\n      test-on-return: false\r\n      test-while-idle: true\r\n      time-between-eviction-runs-millis: 60000  #配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\r\n      min-evictable-idle-time-millis: 300000    #配置一个连接在池中最小生存的时间，单位是毫秒\r\n      filters: stat,wall\r\n      filter:\r\n        wall:\r\n          enabled: true\r\n          config:\r\n            commentAllow: true\r\n            multiStatementAllow: true\r\n            noneBaseStatementAllow: true\r\n      web-stat-filter:  # WebStatFilter配置，说明请参考Druid Wiki，配置_配置WebStatFilter\r\n        enabled: true\r\n        url-pattern: /*\r\n        exclusions: \"*.js , *.gif ,*.jpg ,*.png ,*.css ,*.ico , /druid/*\"\r\n        session-stat-max-count: 1000\r\n        profile-enable: true\r\n        session-stat-enable: false\r\n      stat-view-servlet:  #展示Druid的统计信息,StatViewServlet的用途包括：1.提供监控信息展示的html页面2.提供监控信息的JSON API\r\n        enabled: true\r\n        url-pattern: /druid/*   #根据配置中的url-pattern来访问内置监控页面，如果是上面的配置，内置监控页面的首页是/druid/index.html例如：http://127.0.0.1:9000/druid/index.html\r\n        reset-enable: true    #允许清空统计数据\r\n        login-username: pinda\r\n        login-password: pinda\r\n\r\nmybatis-plus:\r\n  mapper-locations:\r\n    - classpath*:mapper_**/**/*Mapper.xml\r\n  #实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.itheima.pinda.*.entity;com.itheima.pinda.database.mybatis.typehandler\r\n  global-config:\r\n    db-config:\r\n      id-type: INPUT\r\n      insert-strategy: NOT_NULL\r\n      update-strategy: NOT_NULL\r\n      select-strategy: NOT_EMPTY\r\n  configuration:\r\n    #配置返回数据库(column下划线命名&&返回java实体是驼峰命名)，\r\n    #自动匹配无需as（没开启这个，SQL需要写as： select user_id as userId）\r\n    map-underscore-to-camel-case: true\r\n    cache-enabled: false\r\n    #配置JdbcTypeForNull, oracle数据库必须配置\r\n    jdbc-type-for-null: \'null\'\r\n', 'a23f0b95d443fa068362c29fd3fb5b39', '2021-07-22 16:04:32', '2021-07-22 16:04:32', NULL, '222.191.229.234', '', 'pinda-dev', NULL, NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (200, 'pd-auth-server-dev.yml', 'DEFAULT_GROUP', '# p6spy是一个开源项目，通常使用它来跟踪数据库操作，查看程序运行过程中执行的sql语句\r\n# 开发环境需要使用p6spy进行sql语句输出\r\n# 但p6spy会有性能损耗，不适合在生产线使用，故其他环境无需配置\r\nspring:\r\n  datasource:\r\n    driver-class-name: com.p6spy.engine.spy.P6SpyDriver\r\n    url: jdbc:p6spy:mysql://${pinda.mysql.ip}:${pinda.mysql.port}/${pinda.mysql.database}?serverTimezone=CTT&characterEncoding=utf8&useUnicode=true&useSSL=false&autoReconnect=true&zeroDateTimeBehavior=convertToNull&allowMultiQueries=true\r\n    db-type: mysql\r\n', '9065a0332fbdba347cdf530ff2afe99f', '2021-07-22 16:04:32', '2021-07-22 16:04:32', NULL, '222.191.229.234', '', 'pinda-dev', NULL, NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (201, 'pd-auth-server.yml', 'DEFAULT_GROUP', '# 在这里配置 权限服务 所有环境都能使用的配置\r\npinda:\r\n  mysql:\r\n    database: pd_auth\r\n  swagger:\r\n    enabled: true\r\n    docket:\r\n      auth:\r\n        title: 权限模块\r\n        base-package: com.itheima.pinda.authority.controller.auth\r\n      common:\r\n        title: 公共模块\r\n        base-package: com.itheima.pinda.authority.controller.common\r\n      core:\r\n        title: 组织岗位模块\r\n        base-package: com.itheima.pinda.authority.controller.core\r\n\r\nauthentication:\r\n  user:\r\n    header-name: token\r\n    expire: 43200               # 外部token有效期为12小时\r\n    pri-key: client/pri.key    # 加密\r\n    pub-key: client/pub.key    # 解密\r\n\r\nserver:\r\n  port: 8764\r\n', '40188236b25255fd3aec6d911dc32194', '2021-07-22 16:04:32', '2021-07-22 16:04:32', NULL, '222.191.229.234', '', 'pinda-dev', NULL, NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (202, 'pd-gateway.yml', 'DEFAULT_GROUP', 'pinda:\r\n  log:\r\n    enabled: false\r\n\r\nspring:\r\n  mvc:\r\n    servlet:\r\n      path: /gate\r\n\r\nserver:\r\n  port: 8760\r\n  servlet:\r\n    context-path: /api  # = server.servlet.context-path\r\n\r\nzuul:\r\n  #  debug:\r\n  #    request: true\r\n  #  include-debug-header: true\r\n  retryable: false\r\n  servlet-path: /         # 默认是/zuul , 上传文件需要加/zuul前缀才不会出现乱码，这个改成/ 即可不加前缀\r\n  ignored-services: \"*\"   # 忽略eureka上的所有服务\r\n  sensitive-headers:  # 一些比较敏感的请求头，不想通过zuul传递过去， 可以通过该属性进行设置\r\n  #  prefix: /api #为zuul设置一个公共的前缀\r\n  #  strip-prefix: false     #对于代理前缀默认会被移除   故加入false  表示不要移除\r\n  routes:  # 路由配置方式\r\n    authority:  # 其中 authority 是路由名称，可以随便定义，但是path和service-id需要一一对应\r\n      path: /authority/**\r\n      serviceId: pd-auth-server\r\n    file:\r\n      path: /file/**\r\n      serviceId: pd-file-server\r\n\r\n\r\nauthentication:\r\n  user:\r\n    header-name: token\r\n    pub-key: client/pub.key    # 解密\r\n', 'fedfb2b57249ee0ecd380a203cb17ddb', '2021-07-22 16:04:32', '2021-07-22 16:04:32', NULL, '222.191.229.234', '', 'pinda-dev', NULL, NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (203, 'redis.yml', 'DEFAULT_GROUP', '# redis 通用配置， 不同的环境，需要配置不同的链接信息，\r\n# 只需要将这段信息复制到具体环境的配置文件中进行修改即可\r\n# 如：复制到pd-auth-server-dev.yml中将数据库名和ip改掉\r\npinda:\r\n  redis:\r\n    ip: 127.0.0.1\r\n    port: 6379\r\n    password:\r\n    database: 0\r\n\r\nspring:\r\n  cache:\r\n    type: GENERIC\r\n  redis:\r\n    host: ${pinda.redis.ip}\r\n    password: ${pinda.redis.password}\r\n    port: ${pinda.redis.port}\r\n    database: ${pinda.redis.database}\r\n\r\nj2cache:\r\n  #  config-location: /j2cache.properties\r\n  open-spring-cache: true\r\n  cache-clean-mode: passive\r\n  allow-null-values: true\r\n  redis-client: lettuce\r\n  l2-cache-open: true\r\n  # l2-cache-open: false     # 关闭二级缓存\r\n  broadcast: net.oschina.j2cache.cache.support.redis.SpringRedisPubSubPolicy\r\n  #  broadcast: jgroups       # 关闭二级缓存\r\n  L1:\r\n    provider_class: caffeine\r\n  L2:\r\n    provider_class: net.oschina.j2cache.cache.support.redis.SpringRedisProvider\r\n    config_section: lettuce\r\n  sync_ttl_to_redis: true\r\n  default_cache_null_object: false\r\n  serialization: fst\r\ncaffeine:\r\n  properties: /j2cache/caffeine.properties   # 这个配置文件需要放在项目中\r\nlettuce:\r\n  mode: single\r\n  namespace:\r\n  storage: generic\r\n  channel: j2cache\r\n  scheme: redis\r\n  hosts: ${pinda.redis.ip}:${pinda.redis.port}\r\n  password: ${pinda.redis.password}\r\n  database: ${pinda.redis.database}\r\n  sentinelMasterId:\r\n  maxTotal: 100\r\n  maxIdle: 10\r\n  minIdle: 10\r\n  timeout: 10000', '38c69f8b6607c385c19f6a0b299b2860', '2021-07-22 16:04:32', '2021-07-22 16:04:32', NULL, '222.191.229.234', '', 'pinda-dev', NULL, NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (205, 'laputa-codegen.yml', 'DEFAULT_GROUP', '## spring security 配置\r\nsecurity:\r\n  oauth2:\r\n    client:\r\n      client-id: ENC(wftU2ukw3eX1kPyOwfWG4w==)\r\n      client-secret: ENC(wftU2ukw3eX1kPyOwfWG4w==)\r\n      scope: server\r\n# 数据源\r\nspring:\r\n  datasource:\r\n    type: com.alibaba.druid.pool.DruidDataSource\r\n    druid:\r\n      driver-class-name: com.mysql.cj.jdbc.Driver\r\n      username: ${MYSQL_USER:root}\r\n      password: ${MYSQL_PWD:Laputa@123456}\r\n      url: jdbc:mysql://${MYSQL_HOST:laputa-mysql}:${MYSQL_PORT:3306}/${MYSQL_DB:laputa_iot_codegen}?characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=false&useJDBCCompliantTimezoneShift=true&useLegacyDatetimeCode=false&serverTimezone=GMT%2B8&allowMultiQueries=true&allowPublicKeyRetrieval=true\r\n  resources:\r\n    static-locations: classpath:/static/,classpath:/views/\r\n\r\nlaputa:\r\n  tenant:\r\n    column: tenant_id\r\n    tables:\r\n      - gen_datasource_conf', '6cf6c4e2c6aaf32d614f93d3fc6dd460', '2021-07-23 10:31:55', '2021-07-23 10:31:55', NULL, '222.191.229.234', '', 'laputa-iot-new', '不加开发环境的版本', NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (212, 'cache.yml', 'DEFAULT_GROUP', '# redis 通用配置， 不同的环境，需要配置不同的链接信息，\n# 只需要将这段信息复制到具体环境的配置文件中进行修改即可\n# 如：复制到laputa-auth-server-dev.yml中将数据库名和ip改掉\nlaputa:\n  redis:\n    ip: laputa-redis\n    port: 6379\n    password: Laputa@123456\n    database: 0\n\nspring:\n  cache:\n    type: GENERIC\n  redis:\n    host: ${laputa.redis.ip}\n    password: ${laputa.redis.password}\n    port: ${laputa.redis.port}\n    database: ${laputa.redis.database}\n\nj2cache:\n  #  config-location: /j2cache.properties\n  open-spring-cache: true\n  cache-clean-mode: passive\n  allow-null-values: true\n  redis-client: lettuce\n  l2-cache-open: true\n  # l2-cache-open: false     # 关闭二级缓存\n  broadcast: net.oschina.j2cache.cache.support.redis.SpringRedisPubSubPolicy\n  #  broadcast: jgroups       # 关闭二级缓存\n  L1:\n    provider_class: caffeine\n  L2:\n    provider_class: net.oschina.j2cache.cache.support.redis.SpringRedisProvider\n    config_section: lettuce\n  sync_ttl_to_redis: true\n  default_cache_null_object: false\n  serialization: fst\ncaffeine:\n  properties: /j2cache/caffeine.properties   # 这个配置文件需要放在项目中\nlettuce:\n  mode: single\n  namespace:\n  storage: generic\n  channel: j2cache\n  scheme: redis\n  hosts: ${laputa.redis.ip}:${pinda.redis.port}\n  password: ${laputa.redis.password}\n  database: ${laputa.redis.database}\n  sentinelMasterId:\n  maxTotal: 100\n  maxIdle: 10\n  minIdle: 10\n  timeout: 10000', '8b17023329d7acfb19a79dd34a4aad98', '2021-08-11 08:24:42', '2021-08-11 08:25:47', 'nacos', '222.191.229.234', '', 'laputa-iot-dev', '缓存处理', '', '', 'yaml', '');
INSERT INTO `config_info` VALUES (215, 'database.yml', 'DEFAULT_GROUP', ' 个性化配置， 不同的环境，需要配置不同的链接信息，只需要将这段信息复制\r\n# 到具体环境的配置文件中进行修改即可\r\n# 如：复制到laputa-auth-server-dev.yml中将数据库名和ip改掉\r\nlaputa:\r\n  mysql:\r\n    ip: laputa-mysql\r\n    port: 3306\r\n    driverClassName: com.mysql.cj.jdbc.Driver\r\n    database: laputa_iot_upms\r\n    username: root\r\n    password: root\r\n  database:\r\n    isBlockAttack: false  # 是否启用 攻击 SQL 阻断解析器\r\n\r\n# mysql 通用配置\r\nspring:\r\n  datasource:\r\n    druid:\r\n      username: ${MYSQL_USER:laputa.mysql.username}\r\n      password: ${MYSQL_PWD:laputa.mysql.password}\r\n      driver-class-name: ${laputa.mysql.driverClassName}\r\n      url:  jdbc:mysql://${MYSQL_HOST:laputa.mysql.ip}:${MYSQL_PORT:laputa.mysql.port}/${MYSQL_DB:laputa.mysql.database}?characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=false&useJDBCCompliantTimezoneShift=true&useLegacyDatetimeCode=false&serverTimezone=GMT%2B8&allowMultiQueries=true&allowPublicKeyRetrieval=true\r\n      db-type: mysql\r\n      initialSize: 10\r\n      minIdle: 10\r\n      maxActive: 500\r\n      max-wait: 60000\r\n      pool-prepared-statements: true\r\n      max-pool-prepared-statement-per-connection-size: 20\r\n      validation-query: SELECT \'x\'\r\n      test-on-borrow: false\r\n      test-on-return: false\r\n      test-while-idle: true\r\n      time-between-eviction-runs-millis: 60000  #配置间隔多久才进行一次检测，检测需要关闭的空闲连接，单位是毫秒\r\n      min-evictable-idle-time-millis: 300000    #配置一个连接在池中最小生存的时间，单位是毫秒\r\n      filters: stat,wall\r\n      filter:\r\n        wall:\r\n          enabled: true\r\n          config:\r\n            commentAllow: true\r\n            multiStatementAllow: true\r\n            noneBaseStatementAllow: true\r\n      web-stat-filter:  # WebStatFilter配置，说明请参考Druid Wiki，配置_配置WebStatFilter\r\n        enabled: true\r\n        url-pattern: /*\r\n        exclusions: \"*.js , *.gif ,*.jpg ,*.png ,*.css ,*.ico , /druid/*\"\r\n        session-stat-max-count: 1000\r\n        profile-enable: true\r\n        session-stat-enable: false\r\n      stat-view-servlet:  #展示Druid的统计信息,StatViewServlet的用途包括：1.提供监控信息展示的html页面2.提供监控信息的JSON API\r\n        enabled: true\r\n        url-pattern: /druid/*   #根据配置中的url-pattern来访问内置监控页面，如果是上面的配置，内置监控页面的首页是/druid/index.html例如：http://127.0.0.1:9000/druid/index.html\r\n        reset-enable: true    #允许清空统计数据\r\n        login-username: laputa\r\n        login-password: laputa\r\n\r\nmybatis-plus:\r\n  mapper-locations:\r\n    - classpath*:mapper_**/**/*Mapper.xml\r\n  #实体扫描，多个package用逗号或者分号分隔\r\n  typeAliasesPackage: com.laputa.iot.*.entity;com.laputa.iot.database.mybatis.typehandler\r\n  global-config:\r\n    db-config:\r\n      id-type: INPUT\r\n      insert-strategy: NOT_NULL\r\n      update-strategy: NOT_NULL\r\n      select-strategy: NOT_EMPTY\r\n  configuration:\r\n    #配置返回数据库(column下划线命名&&返回java实体是驼峰命名)，\r\n    #自动匹配无需as（没开启这个，SQL需要写as： select user_id as userId）\r\n    map-underscore-to-camel-case: true\r\n    cache-enabled: false\r\n    #配置JdbcTypeForNull, oracle数据库必须配置\r\n    jdbc-type-for-null: \'null\'\r\n', '6a8bac549fd4e439c0748820d292b187', '2021-08-12 08:53:11', '2021-08-12 08:53:11', NULL, '222.191.229.234', '', 'laputa-iot-dev', '云服务器的功能', NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (218, 'application.yml', 'DEFAULT_GROUP', '# 配置文件加密根密码\r\njasypt:\r\n  encryptor:\r\n    password: laputa\r\n    algorithm: PBEWithMD5AndDES\r\n    iv-generator-classname: org.jasypt.iv.NoIvGenerator\r\n\r\nspring:\r\n  redis:\r\n    host: laputa-redis\r\n    password: Laputa@123456\r\n  servlet:\r\n    multipart:\r\n      max-file-size: 100MB\r\n      max-request-size: 100MB\r\n  cloud:\r\n    sentinel:\r\n      eager: true\r\n      filter:\r\n        url-patterns: /** \r\n      transport:\r\n        dashboard: laputa-sentinel:5020\r\n\r\n# 端点对外暴露\r\nmanagement:\r\n  endpoints:\r\n    web:\r\n      exposure:\r\n        include: \'*\'          \r\n  endpoint:\r\n    health:\r\n      show-details: ALWAYS\r\n# feign 相关配置\r\nfeign:\r\n  sentinel:\r\n    enabled: true\r\n  okhttp:\r\n    enabled: true\r\n  httpclient:\r\n    enabled: false\r\n  client:\r\n    config:\r\n      default:\r\n        connectTimeout: 20000\r\n        readTimeout: 20000\r\n  compression:\r\n    request:\r\n      enabled: true\r\n    response:\r\n      enabled: true\r\n\r\n#开启灰度\r\ngray:\r\n  rule:\r\n    enabled: true\r\n\r\n# mybatis-plus 配置\r\nmybatis-plus:\r\n  tenant-enable: ture\r\n  mapper-locations: classpath:/mapper/*Mapper.xml\r\n  global-config:\r\n    banner: false\r\n    db-config:\r\n      db-type: oracle\r\n      id-type: auto\r\n      select-strategy: not_empty\r\n      insert-strategy: not_empty\r\n      update-strategy: not_empty\r\n  type-handlers-package:  com.laputa.iot.common.data.handler\r\n  configuration:\r\n    jdbc-type-for-null: null\r\n    \r\n# swagger 全局配置\r\nswagger:\r\n  enabled: true\r\n  title: Laputa IOT Swagger API\r\n  license: Powered By Laputa\r\n  licenseUrl: https://www.laputa-iot.com/\r\n  terms-of-service-url: https://laputa-iot.com/\r\n  contact:\r\n    email: sommer_jiang@163.com\r\n    url: https://laputa-iot.com/about.html\r\n  authorization:\r\n    name: oauth2\r\n    auth-regex: ^.*$\r\n    authorization-scope-list:\r\n      - scope: server\r\n        description: server all\r\n    token-url-list:\r\n      - http://${GATEWAY_HOST:laputa-gateway}:${GATEWAY_PORT:9999}/auth/oauth/token\r\n\r\n# oauth2 资源服务器相关配置\r\nsecurity:\r\n  oauth2:\r\n    client:\r\n      ignore-urls:\r\n        - /error\r\n        - /actuator/**\r\n        - /v2/api-docs\r\n    resource:\r\n      loadBalanced: true\r\n      token-info-uri: http://laputa-auth/oauth/check_token\r\n      \r\nid-generator:\r\n  machine-code: 1  # id生成器机器掩码', '9832ab9a4641f6bbf075ba22f98d8be2', '2021-08-12 08:56:30', '2021-08-12 08:56:30', NULL, '222.191.229.234', '', 'laputa-iot-dev', 'Laputa Iot的微服务系统的通用配置', NULL, NULL, 'yaml', NULL);
INSERT INTO `config_info` VALUES (223, 'laputa-org-service.yml', 'DEFAULT_GROUP', '## spring security 配置\nsecurity:\n  oauth2:\n    client:\n      client-id: ENC(wftU2ukw3eX1kPyOwfWG4w==)\n      client-secret: ENC(wftU2ukw3eX1kPyOwfWG4w==)\n      scope: server\n      ignore-urls:\n        - /error\n        - /druid/**\n        - /actuator/**\n        - /v2/api-docs\n\n# 数据源\nspring:\n  datasource:\n    type: com.alibaba.druid.pool.DruidDataSource\n    druid:\n      driver-class-name: com.mysql.cj.jdbc.Driver\n      username: ${MYSQL_USER:root}\n      password: ${MYSQL_PWD:Laputa@123456}\n      url: jdbc:mysql://${MYSQL_HOST:laputa-mysql}:${MYSQL_PORT:3306}/${MYSQL_DB:laputa_iot_org}?characterEncoding=utf8&zeroDateTimeBehavior=convertToNull&useSSL=false&useJDBCCompliantTimezoneShift=true&useLegacyDatetimeCode=false&serverTimezone=GMT%2B8&allowMultiQueries=true&allowPublicKeyRetrieval=true\n      stat-view-servlet:\n        enabled: true\n        allow: \"\"\n        url-pattern: /druid/*\n        #login-username: admin\n        #login-password: admin\n      filter:\n        stat:\n          enabled: true\n          log-slow-sql: true\n          slow-sql-millis: 10000\n          merge-sql: false\n        wall:\n          config:\n            multi-statement-allow: true\n\n\n\n# Logger Config\nlogging:\n  level:\n    com.laputa.iot.org.mapper: debug\n\n# 租户表维护\n\nlaputa:\n  tenant:\n    column: tenant_id\n    tables:\n      - org_company\n      - org_department\n      - org_job_grade\n      - org_job_grade_type\n      - org_position_info\n      - org_position_seq\n      - org_role\n      - org_staff\n      - org_staff_role\n\n     ', 'e0a755ac390ad3b07b4a98ed54455797', '2021-08-16 14:33:06', '2021-10-10 12:58:29', 'nacos', '222.191.229.234', '', 'laputa-iot-dev', '', '', '', 'yaml', '');
