INSERT INTO `config_info` VALUES (266, 'spark-admin-dev.yml', 'DEFAULT_GROUP', 'spring:\n  cloud:\n    nacos:\n      discovery:\n        namespace: spark\n        ip: laputa-register\n        port: ${server.port}\n  jackson:\n    default-property-inclusion: non_null\n  datasource:\n    driver-class-name: com.mysql.cj.jdbc.Driver\n    url: jdbc:mysql://laputa-mysql:3306/spark?useUnicode=true&characterEncoding=utf8&useSSL=false&serverTimezone=GMT%2B8\n    username: root\n    password: Laputa@123456\n    name: spark\n    type: com.alibaba.druid.pool.DruidDataSource\n    initial-size: 5\n    min-idle: 5\n    max-active: 20\n    max-wait: 30000\n    time-between-eviction-runs-millis: 60000\n    min-evictable-idle-time-millis: 300000\n    validation-query: select \'1\' from dual\n    test-while-idle: true\n    test-on-borrow: false\n    test-on-return: false\n    pool-prepared-statements: true\n    max-open-prepared-statements: 20\n    max-pool-prepared-statement-per-connection-size: 20\n    filters: stat,wall\n  boot:\n    admin:\n      client:\n        url: http://127.0.0.1:9002\n        instance:\n          prefer-ip: true\n          service-url: http://${spring.cloud.nacos.discovery.ip}:${spring.cloud.nacos.discovery.port}\n        username: spark\n        password: spark\n  redis:\n    host: laputa-redis\n    port: 6379\n    timeout: 10000\n    database: 0\n    password:\n    lettuce:\n      pool:\n        max-active: 300\n        max-idle: 100\n        max-wait: -1\n        min-idle: 20\n      shutdown-timeout: 10000\n\nmybatis-plus:\n  mapper-locations: classpath*:mapper/*.xml\n  type-aliases-package: com.spark.platform.admin.api.entity\n  configuration:\n    log-impl: org.apache.ibatis.logging.stdout.StdOutImpl\n    cache-enabled: false\n    map-underscore-to-camel-case: true\n    use-generated-keys: true\n    default-executor-type: reuse\n    defaultStatementTimeout: 1000\n    jdbc-type-for-null: null\n    call-setters-on-nulls: true\n\nmanagement:\n  metrics:\n    web:\n      server:\n        auto-time-requests: false\n  endpoints:\n    web:\n      exposure:\n        include: \"*\"\n  endpoint:\n    bus-refresh:\n      enabled: true\n\nswagger:\n  base-package: com.spark.platform.admin.biz\n  enabled: true\n  description: spark用户服务\n  version: V1.0\n  license: Apache License, Version 2.0\n  license-url: https://www.apache.org/licenses/LICENSE-2.0.html\n  authorization:\n    key-name: Authorization\n  contact:\n    name: wangdingfeng\n    email: wangdingfeng@live.com\n\nsecurity:\n  oauth2:\n    client:\n      client-id: spark-admin\n      client-secret: spark-admin-secret\n    resource:\n      jwt:\n        key-value: spark_oauth_key\n\nribbon:\n  ReadTimeout: 10000\n  ConnectTimeout: 5000\n  MaxAutoRetries: 1\n  MaxAutoRetriesNextServer: 2\n  eureka:\n    enabled: true\n\nhystrix:\n  command:\n    default:\n      execution:\n        isolation:\n          thread:\n            timeoutInMilliseconds: 60000\n\nfeign:\n  hystrix:\n    enabled: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n\nspark:\n  ignore:\n    urls:\n      - /actuator/**\n      - /druid/**\n      - /**/api/**\n      - /v2/api-docs\n      - /swagger-ui.html\n      - /swagger-resources/**\n      - /webjars/**', '4c37b41fe2304f7604f9ec84c2d79ee3', '2021-09-01 20:21:21', '2021-09-01 20:45:35', 'nacos', '112.87.101.168', '', 'spark', '', '', '', 'yaml', '');
